import React, { useRef, useEffect, useState } from 'react'
import IconButton from '@material-ui/core/IconButton'
import NavigateNextIcon from '@material-ui/icons/NavigateNext'
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore'
import LastPageIcon from '@material-ui/icons/LastPage'
import FirstPageIcon from '@material-ui/icons/FirstPage'
import Tooltip from '@material-ui/core/Tooltip'

import useStyles from './styles'

const FIRST_PAGE = 1

export default function Pagination(props) {
    const { page, perPage, totalPages, goToPage, variant, ...rest } = props,
        classes = useStyles(),
        [isPageTooltipVisisble, setPageTooltipVisible] = useState(false),
        pageEl = useRef(null)

    function isValidPage(currentPage) {
        return (
            currentPage !== page &&
            !!currentPage &&
            Number.isInteger(currentPage) &&
            currentPage >= FIRST_PAGE &&
            currentPage <= totalPages
        )
    }

    function handleGoToPage(page) {
        if (!isValidPage(page)) {
            return
        }

        return goToPage(page)
    }

    function handleGoToFirstPage() {
        return handleGoToPage(FIRST_PAGE)
    }

    function handleGoToLastPage() {
        return handleGoToPage(totalPages)
    }

    function handleGoToPrevPage() {
        return handleGoToPage(page - 1)
    }

    function handleGoToNextPage() {
        return handleGoToPage(page + 1)
    }

    function setPageElText(page) {
        if (pageEl.current) {
            pageEl.current.innerText = page
        }
    }

    function resetPageElText() {
        setPageTooltipVisible(false)
        setPageElText(page)
    }

    function handleInputPage(e) {
        const { innerText: value } = e.target,
            numberValue = parseInt(value)

        if (!isValidPage(numberValue)) {
            return setPageElText(page)
        }

        return handleGoToPage(numberValue)
    }

    function handleKeyDown(e) {
        // NOTE: not doing any expensive operation,
        // so debounce is not required
        if (e.which !== 13) {
            return
        }

        e.preventDefault()
        return handleInputPage(e)
    }

    function handleKeyUp(e) {
        setPageTooltipVisible(isValidPage(parseInt(e.target.innerText)))
    }

    useEffect(resetPageElText, [page])

    // useEffect(() => {
    //     const debouncedHandleKeyUp = debounce(handleKeyUp)

    //     if (pageEl.current) {
    //         pageEl.current.addEventListener('blur', resetPageElText)
    //         pageEl.current.addEventListener('keydown', handleKeyDown)
    //         pageEl.current.addEventListener('keyup', handleKeyUp)
    //     }

    //     return () => {
    //         if (pageEl.current) {
    //             pageEl.current.removeEventListener('blur', resetPageElText)
    //             pageEl.current.removeEventListener('keydown', handleKeyDown)
    //             pageEl.current.removeEventListener('keyup', handleKeyUp)
    //         }
    //     }
    // }, [page, perPage, totalPages, goToPage])

    const iconButtonProps = {
        color: 'inherit',
    }

    return (
        <div {...(variant === 'dark' && { className: classes.dark })}>
            <IconButton onClick={handleGoToFirstPage} {...iconButtonProps}>
                <FirstPageIcon />
            </IconButton>
            <IconButton onClick={handleGoToPrevPage} {...iconButtonProps}>
                <NavigateBeforeIcon />
            </IconButton>
            <span className={classes.text}>
                Showing page{' '}
                <Tooltip
                    title="Hit Enter to go to the page"
                    open={isPageTooltipVisisble}
                    arrow
                    classes={{
                        tooltip: classes.tooltip,
                        arrow: classes.tooltipArrow,
                    }}
                    PopperProps={{ disablePortal: true }}
                >
                    <span contentEditable ref={pageEl} className={classes.input} />
                </Tooltip>{' '}
        of {totalPages}
            </span>
            <IconButton onClick={handleGoToNextPage} {...iconButtonProps}>
                <NavigateNextIcon />
            </IconButton>
            <IconButton onClick={handleGoToLastPage} {...iconButtonProps}>
                <LastPageIcon />
            </IconButton>
        </div>
    )
}
