    const [imdbData, setImdbData] = useState([])
    const [rating,setRating]=useState('')

    useEffect(()=>{
        let url = 'http://www.omdbapi.com/?i=' + props.imdbID + '&plot=full&apikey=328cb361'
        console.log('url',url)
        fetch(url)
            .then(res => res.json())
            .then(res => setImdbData(res))
    },[])


    const showModal = () => {
        console.log('hi fro modal')
