import React, { useRef, useEffect, useState } from 'react'
import IconButton from '@material-ui/core/IconButton'
import NavigateNextIcon from '@material-ui/icons/NavigateNext'
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore'
import LastPageIcon from '@material-ui/icons/LastPage'
import FirstPageIcon from '@material-ui/icons/FirstPage'
import Tooltip from '@material-ui/core/Tooltip'

import useStyles from './styles'

const FIRST_PAGE = 1

export default function Pagination(props) {
    const { page, perPage, totalPages, goToPage, variant, ...rest } = props,
        classes = useStyles(),
        [isPageTooltipVisisble, setPageTooltipVisible] = useState(false),
        pageEl = useRef(null)

    function isValidPage(currentPage) {
        return (
            currentPage !== page &&
            !!currentPage &&
            Number.isInteger(currentPage) &&
            currentPage >= FIRST_PAGE &&
            currentPage <= totalPages
        )
    }

    function handleGoToPage(page) {
        if (!isValidPage(page)) {
            return
        }

        return goToPage(page)
    }

    function handleGoToFirstPage() {
        return handleGoToPage(FIRST_PAGE)
    }

    function handleGoToLastPage() {
        return handleGoToPage(totalPages)
    }

    function handleGoToPrevPage() {
        return handleGoToPage(page - 1)
    }

    function handleGoToNextPage() {
        return handleGoToPage(page + 1)
    }

    function setPageElText(page) {
        if (pageEl.current) {
            pageEl.current.innerText = page
        }
    }

    function resetPageElText() {
        setPageTooltipVisible(false)
        setPageElText(page)
    }

    function handleInputPage(e) {
        const { innerText: value } = e.target,
            numberValue = parseInt(value)

        if (!isValidPage(numberValue)) {
            return setPageElText(page)
        }

        return handleGoToPage(numberValue)
    }





    useEffect(resetPageElText, [page])



    const iconButtonProps = {
        color: 'inherit',
    }

    return (
        <div >
            <IconButton onClick={handleGoToFirstPage} {...iconButtonProps}>
                <FirstPageIcon />
            </IconButton>
            <IconButton onClick={handleGoToPrevPage} {...iconButtonProps}>
                <NavigateBeforeIcon />
            </IconButton>
            <span className={classes.text}>
                Showing page{' '}

                    <span  ref={pageEl}  />
                {' '}
        of {totalPages}
            </span>
            <IconButton onClick={handleGoToNextPage} {...iconButtonProps}>
                <NavigateNextIcon />
            </IconButton>
            <IconButton onClick={handleGoToLastPage} {...iconButtonProps}>
                <LastPageIcon />
            </IconButton>
        </div>
    )
}
